FROM   user_tb
WHERE  usernickname = #{usernickname}

SELECT username, userpassword
FROM   user_tb
WHERE  username = #{username} AND userpassword = #{userpassword}

SELECT userpassword
FROM   user_tb
WHERE  username = #{username}

